#!/usr/bin/python

""" Link: http://rosalind.info/problems/hamm/ """

seq1 = ''
seq2 = ''

def getHammingDistance(seq1, seq2):
    distance = 0
    for i, _ in enumerate(seq1):
        if seq1[i] != seq2[i]:
            distance += 1
    return distance

if __name__ == "__main__":
    hamming_distance = getHammingDistance(seq1, seq2)
    print(hamming_distance)

