#!/usr/bin/python

def get_hamming_distance(seq1, seq2):
    hamm_dist = 0
    #  length = len(seq1)
    for i, _ in enumerate(seq1):
        #  print(seq1[i], seq2[i])
        if seq1[i] != seq2[i]:
            hamm_dist += 1
    return hamm_dist

if __name__ == "__main__":
    seq1 = ''
    seq2 = ''
    hamming_distance = get_hamming_distance(seq1, seq2)
    print(hamming_distance)


